application.expectedCSS:
@font-face {
  font-family: 'Bely Display';
  src: url('https://fonts.example.com/bely-display');
  font-weight: 400 700;
}

@font-face {
  font-family: 'Inter';
  src: url('https://fonts.googleapis.com/css2?family=Inter:wght@300..700');
  font-weight: 300 700;
}

@font-face {
  font-family: 'JetBrains Mono';
  src: url('https://fonts.googleapis.com/css2?family=JetBrains+Mono');
  font-weight: 400 700;
}

h1, h2, h3, h4, h5, h6 {
  font-family: 'Bely Display', Georgia, serif;
  text-transform: lowercase;
  letter-spacing: 0.05em;
}

body, p, span, div {
  font-family: Inter, system-ui, sans-serif;
  font-size: 16px;
  line-height: 1.5;
}

code, pre {
  font-family: 'JetBrains Mono', monospace;
}
